import React from 'react';
import styled from "styled-components";
import { Col, Input, Menu, Row } from 'antd';
import Link from 'next/link';
import {FullscreenExitOutlined } from '@ant-design/icons'


const Header = styled.header`
  width: 100%;
  border: 0;
  position: fixed;
  top: 0;
  left: 0;
  background-color: white;
  border-bottom: ${props => props.theme.boxBorder};
  border-radius: 0px;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 25px 0px;
  z-index: 2;
`;

const HeaderWrapper = styled.div`
  width: 100%;
  max-width: ${props => props.theme.maxWidth};
  display: flex;
  justify-content: center;
`;

const HeaderColumn = styled.div`
  width: 33%;
  text-align: center;
  &:first-child {
    margin-right: auto;
    text-align: left;
  }
  &:last-child {
    margin-left: auto;
    text-align: right;
  }
`;

const SearchInput = styled(Input)`
  background-color: ${props => props.theme.bgColor};
  padding: 5px;
  font-size: 14px;
  border-radius: 3px;
  height: auto;
  text-align: center;
  width: 70%;
  &::placeholder {
    opacity: 0.8;
    font-weight: 200;
  }
`;

const HeaderLink = styled(Link)`
  &:not(:last-child) {
    margin-right: 30px;
  }
`;

const AppLayout = ({children}) => {
    
    return (
        <>
                <Header>
            <HeaderWrapper>
                <HeaderColumn>
                <Link to="/">
                    <Logo />
                </Link>
                </HeaderColumn>
                <HeaderColumn>
                <form onSubmit={onSearchSubmit}>
                    <SearchInput 
                    value={search.value}
                    placeholder="검색" 
                    onChange={search.onChange}
                    />
                </form>
                </HeaderColumn>
                <HeaderColumn>
                <HeaderLink to="/explore">
                    <Compass />
                </HeaderLink>
                <HeaderLink to="/notifications">
                    <HeartEmpty />
                </HeaderLink>
                
                {!(data && data.me) ? (
                    <HeaderLink to="/#">
                        <User />
                    </HeaderLink>
                ) : (
                    <HeaderLink to={data.me.username}>
                        <User />
                    </HeaderLink>
                ) }
                </HeaderColumn>
            </HeaderWrapper>
            </Header>
            <div>
                {children}
            </div>
        </>
    )

};

export default AppLayout;
